if(context.global.keyboard.pending)
{
    context.global.keyboard.pending = false;

    if(msg.payload.content === 'Yes')
    {
        msg.payload.content = 'Yes';
        return [msg, null];   
    }
    else
    {
        msg.payload.content = 'No';
        return [null, msg];   
    }
}

// Here you must adapt the chatId.
var chatId = 123;

context.global.keyboard = { pending : true };

var opts = {
  reply_to_message_id: 0,
  reply_markup: JSON.stringify({
    keyboard: [
      ['Yes'],
      ['No']],
      'resize_keyboard' : true, 
      'one_time_keyboard' : true
  })
};

var payload = {
    chatId : chatId,
    type : 'message',
    content : 'Really?',
    options : opts,
};


msg.payload = payload;
return [ msg ];
